import React ,{useState} from "react";
import classes from "./todosInput.module.css";
const TodosInput = (props) =>{
    
    const [todos,setTodos] = useState([]);

    function addItem(todos, name) {
        let item = { id: `id-${name}`, name: name, active: true, isEditable: false };
        console.log(item);
        todos.push(item);
        console.log(todos);
        return todos;
      }

    function addItemUpd(event){
        if(event.code == "Enter"){
            setTodos(addItem(todos, event.target.value));    
        }
    }  

    // function onInputChange(e) {
    //     setTodo(addItem(todo, e.target.value));
    // }

    return <div>
        <input 
        className={classes.todosInput} 
        type="text" 
        placeholder="What need to be done?"
        todos={todos} 
        onKeyUp={event=>addItemUpd(event)}
        />
{/* onChange={event=>onInputChange(event)} */}

    </div>;
}

export default TodosInput;
