import React ,{ useState , useEffect } from "react";
import Header from "./Util/header";
import Input from "./Components/todosInput";
import Footer from "./Util/footer";
import TodosItems from "./Components/todosItems";
// import {TodosContext} from "./todos-context";
// import {TodosContext} from "./Context/todosContext";

export const TodosContext = React.createContext();

function App() {
  
    const [todos,setTodos] = useState([]);
    const [todo,setTodo] = useState("");
    const [lstCount,setLstCount] = useState();
    const tabs = ['All','Active','Completed'];
    
    // const providerValues = {
    //   todos:todos,
    //   setTodo:setTodos,
    //   saveTodos:saveTodos,
    //   // setValue:(todos)=>setTodos(todos),
    //   // saveValue:(todos)=>saveTodos(todos),
    // }

    // localStorage.clear();
    useEffect(()=>{
      let todosFromStroage = localStorage.getItem("Todos");
      if(todosFromStroage){
        console.log(todosFromStroage);
        setLstCount(JSON.parse(todosFromStroage).length);
        setTodos(JSON.parse(todosFromStroage));
        // setLstCount(JSON.parse(todosFromStroage).length);
        console.log(todos);
      }
      else{
        setTodos(todos);
        setLstCount(todos.length);
      }
    },[todo,lstCount])
    
    function saveTodos(todos){
        localStorage.setItem("Todos",JSON.stringify(todos));
    }
    
    function addItem(td, name) {
      let item = { id: `id-${name}`, name: name, active: true, isEditable: false };
          
      td.push(item);
      console.log(td);
      saveTodos(td);
      setTodo(item);
    }
  
    // value={{
    //   todos:todos
    // }}
    // {todos:todos,tabs:tabs,newTodo:newTodo}
    // todos:todos,tabs:tabs,setTodos:setTodos
    // {todos:todos,setTodo:setTodo,saveTodos:saveTodos}
    // providerValues
    return (
      <TodosContext.Provider value={{todos:todos,setTodo:setTodo,setTodos:setTodos,saveTodos:saveTodos,updLstCount:setLstCount}}>  
            <Header title={"todos"}/>
            <div style={{width:"50vw"}}>
            <Input todos={todos} addItem={addItem}/>
            <TodosItems todos={todos}/>
            <Footer tabs={tabs} todos={todos} len={lstCount} />
            </div>
      </TodosContext.Provider>
      
    );  
  }
  
  export default App;
