import React ,{useState} from "react";
import classes from "./todosInput.module.css";
const TodosInput = (props) =>{
    
    const [inputVal,setInputVal] = useState("");

    // function addItem(todos, name) {
    //     let item = { id: `id-${name}`, name: name, active: true, isEditable: false };
    //     console.log(item);
    //     todos.push(item);
    //     console.log(todos);
    //     return todos;
    //   }

    function addItemUpd(event){
        if(event.code === "Enter"){
            props.addItem(props.todos,event.target.value);
            event.target.value = "";    
            setInputVal(inputVal);
        }
    }  

    return <div>
        <input 
        className={classes.todosInput} 
        type="text" 
        placeholder="What need to be done?"
        value={inputVal}
        // todos={props.todos} 
        onKeyUp={event=>addItemUpd(event)}
        />
    </div>;
}

export default TodosInput;
