    // }

    // isChecked={isChecked(todoCtx)}
    // data-id={todosContext.id}
    return <li className={classes.todosItem}>
        <div>
        <input type="checkbox" />
        {props.todo.name}
        </div>
        <button>&times;</button>
    </li>
    // onclick={onClickEvent}
}

export default TodosItem;
