import React ,{useState, useContext, useEffect} from "react";
import classes from "./footer.module.css";
import {TodosContext} from "../App";

const Footer = (props) =>{

    const todosCtx = useContext(TodosContext);
    console.log(props.todos.length);
    todosCtx.todos.map(td=>console.log(td));
    
    const getTabHandler = (e) => {
    
        let tab = e.target.dataset.name;
        console.log(tab);
        // todosCtx.setTab(tab); 
        let initTabsArr = props.tabs.map(t => {
            t.active = false;
            return t;
        });
        let tabIndex = props.tabs.indexOf(props.tabs.find(tb=>tb.name == tab));
        initTabsArr[tabIndex].active = true;
        todosCtx.setTab(tab);
    }

    // const tabArrHandler
    // tab.active==true?classes.tabSelected:classes.tabNotSelected
    let tabs = props.tabs.map((tab,ind)=>{
        // console.log()
        return <li key={ind} style={{background:"white"}}>
            <a href={`#${tab.name}`} 
               data-name={tab.name} 
               onClick={(event) => getTabHandler(event)} 
               className={tab.active==true?classes.tabSelected:classes.tabNotSelected}>{tab.name}</a></li>
    })

    return <footer className={classes.footer}>
        <p><span id="listcount"></span>{props.len} item left</p>
        <ul>{tabs}</ul>
    </footer>
}

export default Footer;
