import React, { useContext } from "react";
import classes from "./todosItem.module.css";
import TodosContext from "../todos-context";
import todosContext from "../todos-context";

const TodosItem = (props) => {
    // console.log()
    let todoCtx = useContext(todosContext);
    console.log(`todoContex : ${todosContext}`);
    const isChecked = (todos) => {
        return todos.isActive == true ? "" : "checked";
    }

    return <li className={classes.todosItem}>
        <div>
        <input type="checkbox" data-id={todosContext.id} isChecked={isChecked(todoCtx)}/>
        {props.todo.name}
        </div>
        <button>&times;</button>
    </li>
    // onclick={onClickEvent}
}

export default TodosItem;
