import React ,{useState, useContext, useEffect} from "react";
import classes from "./footer.module.css";
// import todosCtx from 
import {TodosContext} from "../App";
const Footer = (props) =>{

    const [lstCount,setLstCount] = useState();
    const todosCtx = useContext(TodosContext);
    console.log(props.todos.length);
    // todosCtx.todos.map(td=>console.log(td));
    useEffect(()=>{
        if(JSON.parse(localStorage.getItem("Todos"))){
            setLstCount(JSON.parse(localStorage.getItem("Todos")).length);
        }
        else{
            setLstCount(0);
        }
        
    },[])

    const getActiveListArray = (todos) => {
        return todos.filter(td=>{
            return td.active == true; 
        })
    }

    const getNotActiveListArray = (todos) => {
        return todos.filter(td=>{
            return td.active == false; 
        })
    }

    const  getListByStatus = (e) => {
        let tab = e.target.dataset.name;
        let todoArrNew = [];
        todoArrNew = JSON.parse(localStorage.getItem("Todos"));
        console.log(todoArrNew);
        if(tab == "Completed") {
            todoArrNew = getNotActiveListArray(todoArrNew);     
        }
        if(tab == "Active") {
            todoArrNew = getActiveListArray(todoArrNew);
        }
        
        console.log(todoArrNew);
        // todosCtx.saveTodos(todoArrNew);
        // console.log(todoArrNew);
        todosCtx.setTodos(todoArrNew);
        setLstCount(todoArrNew.length);            
    }        

    let tabs = props.tabs.map((tab,ind)=>{
        return <li key={ind} className={tab==props.tab?classes.tabSelected:null}>
            <a href={`#${tab}`} data-name={tab} onClick={(event) => getListByStatus(event)}>{tab}</a></li>
    })

    return <footer className={classes.footer}>
        <p><span id="listcount"></span>{lstCount} item left</p>
        <ul>{tabs}</ul>
    </footer>
}

export default Footer;
