import React ,{useContext} from "react";
import classes from "./footer.module.css";
// import todosCtx from 
import {TodosContext} from "../App";
const Footer = (props) =>{

    const todosCtx = useContext(TodosContext);
    
    const getActiveListArray = () => {
        return todosCtx.filter(td=>{
            return td.active == true; 
        })
    }

    const getNotActiveListArray = () => {
        return todosCtx.filter(td=>{
            return td.active == false; 
        })
    }

    const  getListByStatus = (e) => {
        let tab = e.target.dataset.name;
        let todoArrNew = todosCtx;
        if (tab == "Completed") todoArrNew = getNotActiveListArray(); 
        if (tab == "Active") todoArrNew = getActiveListArray();
        return todoArrNew;            
    }        

    let tabs = props.tabs.map((tab,ind)=>{
        return <li key={ind} className={tab==props.tab?classes.tabSelected:null}>
            <a href={`#${tab}`} data-name={tab} onClick={(event) => getListByStatus(event)}>{tab}</a></li>
    })

// function getListByStatus(todos, tab) {
//     let todoArrNew = [];
//     if (tab == "Completed") {
//       todoArrNew = getNotActiveListArray(todos);
//     } else if (selectedTab == "Active") {
//       todoArrNew = getActiveListArray(todos);
//     } else {
//       todoArrNew = todos;
//     }
//     return todoArrNew;
//   }

// let tabs = ["All", "Active", "Completed"]
//     .map( 
//       (val) =>
//         `<li class="todo--inline ${
//           val == tab && "todo__tab--selected"
//         }" onClick="onTabClickEvent(event)"><a href="#${val}" class="todo__tab">${val}</a></li>`
//     )
//     .join();

    let listcount = (todos) => {
        let todosNew = todos.filter((todo) => todo.active == true);
        return todosNew.length;
    }

    return <footer className={classes.footer}>
        <p><span id="listcount"></span>{listcount(props.todos)} item left</p>
        <ul>{tabs}</ul>
    </footer>
}

export default Footer;
