import React ,{useState, useContext, useEffect} from "react";
import classes from "./footer.module.css";
// import todosCtx from 
import {TodosContext} from "../App";
const Footer = (props) =>{

    const [lstCount,setLstCount] = useState();
    const todosCtx = useContext(TodosContext);
    console.log(todosCtx.todos.length);
    // todosCtx.todos.map(td=>console.log(td));
    useEffect(()=>{
        setLstCount(todosCtx.todos.length);
    },[])

    // todosCtx.todos.map(td=>console.log(td));

    const getActiveListArray = (todos) => {
        return todos.filter(td=>{
            return td.active == true; 
        })
    }

    const getNotActiveListArray = (todos) => {
        return todos.filter(td=>{
            return td.active == false; 
        })
    }

    const  getListByStatus = (e) => {
        let tab = e.target.dataset.name;
        let todoArrNew = [];
        if (tab == "Completed") {
            todoArrNew = getNotActiveListArray(todosCtx.todos);     
        }
        else if (tab == "Active") {
            todoArrNew = getActiveListArray(todosCtx.todos);
        }
        else{
            todoArrNew = todosCtx.todos;
        }

        console.log(todoArrNew);
        todosCtx.saveTodos(todoArrNew);
        console.log(todoArrNew);
        // todosCtx.setTodo(todoArrNew);
        setLstCount(listcount(todoArrNew));            
    }        

    let tabs = props.tabs.map((tab,ind)=>{
        return <li key={ind} className={tab==props.tab?classes.tabSelected:null}>
            <a href={`#${tab}`} data-name={tab} onClick={(event) => getListByStatus(event)}>{tab}</a></li>
    })

    // data-name={tab} onClick={(event) => getListByStatus(event)}

// function getListByStatus(todos, tab) {
//     let todoArrNew = [];
//     if (tab == "Completed") {
//       todoArrNew = getNotActiveListArray(todos);
//     } else if (selectedTab == "Active") {
//       todoArrNew = getActiveListArray(todos);
//     } else {
//       todoArrNew = todos;
//     }
//     return todoArrNew;
//   }

// let tabs = ["All", "Active", "Completed"]
//     .map( 
//       (val) =>
//         `<li class="todo--inline ${
//           val == tab && "todo__tab--selected"
//         }" onClick="onTabClickEvent(event)"><a href="#${val}" class="todo__tab">${val}</a></li>`
//     )
//     .join();

    let listcount = (todos) => {
        // let todosNew = todos.filter((todo) => todo.active == true);
        return todos.length;
    }

    return <footer className={classes.footer}>
        <p><span id="listcount"></span>{lstCount} item left</p>
        <ul>{tabs}</ul>
    </footer>
}

export default Footer;
