import React from "react";
import ReactDOM from "react-dom";
import { useState , useEffect } from "react";
import Header from "./Util/header";
import Input from "./Components/todosInput";
import Footer from "./Util/footer";

ReactDOM.render(<App />, document.getElementById("root"));

function App() {

  const [todos,setTodos] = useState([]);
  const tabs = ['All','Active','Completed'];

  useEffect(()=>{
    let todosFromStroage = localStorage.getItem("Todos");
    localStorage.getItem("Todos")?setTodos(JSON.parse(todosFromStroage)):
    setTodos(todos);
  },[])

  function saveTodos(todos){
      localStorage.setItem("Todos",JSON.stringify(todos));
  }

  function addItem(todos, name) {
    let item = { id: `id-${name}`, name: name, active: true, isEditable: false };
    console.log(item);
    todos.push(item);
    console.log(todos);
    saveTodos(todos);
    // return todos;
  }

  return (
    <main>
      <Header title={"todos"}/>
      <Input todos={todos} addItem={addItem}/>
      <Footer tabs={tabs} todos={todos}/>
    </main>
  );


}
