import React ,{useState, useContext, useEffect} from "react";
import classes from "./footer.module.css";
import {TodosContext} from "../App";
import styled from "styled-components";

const Footer = (props) =>{

    // const TabStyled = styled.button`
    //    border:${props.tab.active==true?"2px solid blue":"1px solid transperent"}
    // `;

    const todosCtx = useContext(TodosContext);
    console.log(props.todos.length);
    todosCtx.todos.map(td=>console.log(td));
    
    const getTabHandler = (e) => {
    
        let tab = e.target.dataset.name;
        todosCtx.setTab(tab); 
        // let initTabsArr = props.tabs.map(t => {t.active = false});
        // let tabIndex = props.tabs.indexOf(tab);
        // initTabsArr[tabIndex].active = true;

    }

    
    let tabs = props.tabs.map((tab,ind)=>{
        return <li key={ind} style={tab==props.tab?"border: 2px solid blue":""}>
            <a href={`#${tab}`} data-name={tab} onClick={(event) => getTabHandler(event)}>{tab}</a></li>
    })

    return <footer className={classes.footer}>
        <p><span id="listcount"></span>{props.len} item left</p>
        <ul>{tabs}</ul>
    </footer>
}

export default Footer;
