import classes from "./todosInput.module.css";
const todosInput = (props) =>{
    
    function onInputChange(e) {
        setState(addItem(todoArr, e.target.value));
    }

    return <div><input className={classes.todosInput} type="text" placeholder="What need to be done?" onChange={onInputChange(event)}/>

    </div>;
}

export default todosInput;
