import React ,{ useState , useEffect } from "react";
import Header from "./Util/header";
import Input from "./Components/todosInput";
import Footer from "./Util/footer";
import TodosItems from "./Components/todosItems";
// import {TodosContext} from "./todos-context";
// import {TodosContext} from "./Context/todosContext";

export const TodosContext = React.createContext();

function App() {
  
    const tabs = ['All','Active','Completed'];
    const [todos,setTodos] = useState([]);
    const [todo,setTodo] = useState("");
    const [lstCount,setLstCount] = useState(0);
    const [tab,setTab] = useState("");
    const [toggle,setToggle] = useState(0);    
    // const getActiveListArray = (todos) => {
    //   return todos.filter(td=>{
    //       return td.active == true; 
    //   })
    // }

    // const providerValues = {
    //   todos:todos,
    //   setTodo:setTodos,
    //   saveTodos:saveTodos,
    //   // setValue:(todos)=>setTodos(todos),
    //   // saveValue:(todos)=>saveTodos(todos),
    // }
    
    const getActiveListArray = (todos) => {
      return todos.filter(td=>{
          return td.active == true; 
      })
  }

  const getNotActiveListArray = (todos) => {
      return todos.filter(td=>{
          return td.active == false; 
      })
  }

  const  getListByStatus = (todosArr) => {
      let todoArrNew = JSON.parse(localStorage.getItem("Todos"));
      console.log(tab);
      if(tab == "Completed") {
        console.log(todoArrNew);
          todoArrNew = getNotActiveListArray(todoArrNew);     
      }
      if(tab == "Active"){
          todoArrNew = getActiveListArray(todoArrNew);
      }
      // if(tab == "Active") {
      //   console.log(todoArrNew);
      //     todoArrNew = getActiveListArray(todoArrNew);
      // }
      console.log(todoArrNew);
      return todoArrNew;
    }

    const getRnewTodos = (todos) => {
      const todosEvery = getListByStatus(todos);
      console.all(tab)
      if(tab=="All"){
        setLstCount(getNotActiveListArray(todos).length);
      }
      else{
        setLstCount(todosEvery.length);
      }
      
      
      if(tab!=""){
        setTodos(todosEvery);
      }
      else
        setTodos(JSON.parse(localStorage.getItem("Todos")));
      
    }

    // localStorage.clear();
    useEffect(()=>{
      let todosFromStroage = localStorage.getItem("Todos");
      console.log("ddddddddddddddddddddddddddddddddddddddddddddddddddddddddd"+todosFromStroage);
      if(todosFromStroage){
        console.log(todosFromStroage);
      
        // setLstCount(getListByStatus(todosFromStroage));
        
        getRnewTodos(todosFromStroage);
        // setTodos(getListByStatus(todosFromStroage));
      }
      else{
        // setLstCount(todos.length);
        setTodos(todos);
        // setLstCount(todos.length);
      }
    },[todo,tab])
    
    function saveTodos(todos){
        console.log(JSON.stringify(todos));
        localStorage.setItem("Todos",JSON.stringify(todos));
    }
    
    function addItem(td, name) {
      let item = { id: `id-${name}`, name: name, active: true, isEditable: false };
          
      td.push(item);
      console.log(td);
      saveTodos(td);
      setTodo(item);
    }
  
    // const todoLen = () =>{
    //   return getActiveListArray(todos).length;
    // }
    // value={{
    //   todos:todos
    // }}
    // {todos:todos,tabs:tabs,newTodo:newTodo}
    // todos:todos,tabs:tabs,setTodos:setTodos
    // {todos:todos,setTodo:setTodo,saveTodos:saveTodos}
    // providerValues
    return (
      <TodosContext.Provider value={{todos:todos,setTodo:setTodo,setTodos:setTodos,saveTodos:saveTodos,setLstCount:setLstCount,setTab:setTab}}>  
            <Header title={"todos"}/>
            <div style={{width:"50vw"}}>
            <Input todos={todos} addItem={addItem}/>
            <TodosItems todos={todos}/>
            <Footer tabs={tabs} todos={todos} len={lstCount}/>
            </div>
      </TodosContext.Provider>
      
    );  
  }
  
  export default App;
